import axios from "axios";
import React from "react";
import { useContext } from "react";

import MedicineContext from "../context/MedicineContext";

const MedicineCard = ({ id, stock, name, description, photoUrl }) => {
  const { getList } = useContext(MedicineContext);

  const increaseStockHandler = () => {
    axios
      .patch(`http://localhost:6565/medicene/${id}`, {
        stock: stock + 1,
      })
      .then((resp) => {
        getList();
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const decreaseStockHandler = () => {
    axios
      .patch(`http://localhost:6565/medicene/${id}`, {
        stock: stock - 1,
      })
      .then((response) => {
        getList();
      })
      .catch((error) => {
        console.log(error);
      });
  };
  const deleteMedicineHandler = () => {
    axios.delete(`http://localhost:6565/medicene/${id}`).then((response) => {
      getList();
    });
  };
  return (
    <div>
      <div className="card mb-3">
        <div className="row g-0">
          <div className="col-md-4">
            <img src={photoUrl} className="img-fluid rounded-start" alt="..." />
          </div>
          <div className="col-md-8">
            <div className="card-body">
              <div className="row">
                <div className="col-md-9">
                  <h5 className="card-title">{name}</h5>
                </div>
                <div className="col-md-3">
                  <button
                    id="deleteBtn"
                    className="btn-sm btn-danger"
                    onClick={deleteMedicineHandler}
                  >
                    Delete
                  </button>
                </div>
              </div>

              <p className="card-text">{description}</p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
              <button
                className="btn-sm btn-secondary"
                onClick={decreaseStockHandler}
              >
                -
              </button>
              <input type="text" readOnly={true} value={stock} />
              <button
                className="btn-sm btn-secondary"
                onClick={increaseStockHandler}
              >
                +
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MedicineCard;
