import axios from "axios";
import React from "react";
import { useState } from "react";
const MedicineCard = ({ id, stock, name, description, photoUrl }) => {
  const [currentStock, setCurrentStock] = useState(stock);
  const increaseStockHandler = () => {
    axios.patch(`http://localhost:6565/medicene/${id}`, {
      stock: stock + 1,
    });
    setCurrentStock(stock + 1);
  };
  return (
    <div>
      <div className="card mb-3">
        <div className="row g-0">
          <div className="col-md-4">
            <img src={photoUrl} className="img-fluid rounded-start" alt="..." />
          </div>
          <div className="col-md-8">
            <div className="card-body">
              <h5 className="card-title">{name}</h5>
              <p className="card-text">{description}</p>
              <p className="card-text">
                <small className="text-muted">Last updated 3 mins ago</small>
              </p>
              <button className="btn-sm btn-danger">-</button>
              <input type="text" readOnly={true} value={currentStock} />
              <button
                className="btn-sm btn-primary"
                onClick={increaseStockHandler}
              >
                +
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MedicineCard;
